import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // Используем правильный импорт
import Home from './pages/Home';
import Mixes from './pages/Mixes';
import Table from './pages/Table';
import Regular from './pages/Regular';
import Cards from './pages/Cards';
import Workers from './pages/Workers';
import LoginPage from './pages/LoginPage';
import Profile from './pages/Profile';
import Layout from './components/Layout'; // Импортируем общий макет
import './styles.css';

const isAuthenticated = () => !!localStorage.getItem('token');

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />

        {/* Все страницы внутри Layout */}
        <Route element={isAuthenticated() ? <Layout /> : <Navigate to="/login" />}>
          <Route path="/" element={<Home />} />
          <Route path="/home" element={<Home />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/mixes" element={<Mixes />} />
          <Route path="/table" element={<Table />} />
          <Route path="/regular" element={<Regular />} />
          <Route path="/cards" element={<Cards />} />
          <Route path="/workers" element={<Workers />} />
        </Route>

        {/* Редирект на страницу логина или домой */}
        <Route path="*" element={<Navigate to={isAuthenticated() ? "/home" : "/login"} />} />
      </Routes>
    </Router>
  );
}

export default App;
