import React, { useState, useEffect } from 'react';
import './css/home2.css';

const App = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);  // Будем хранить общее количество страниц

  const limit = 5;  // Например, мы ограничиваем количество элементов на странице

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        setLoading(true);
        // Заменим переменную process на явное значение для URL
        const url = `http://192.168.1.66:8000/locations/get_locations?page=${currentPage}&limit=${limit}`;
        const response = await fetch(url);
        const data = await response.json();
        setRestaurants(data.results);  // Например, данные могут быть в поле 'results'
        setTotalPages(Math.ceil(data.total / limit));  // Пример, если есть поле 'total' для общего количества
        setLoading(false);
      } catch (error) {
        console.error("Ошибка при получении данных:", error);
        setLoading(false);
      }
    };

    fetchRestaurants();
  }, [currentPage, limit]);  // Зависим от currentPage и limit

  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="container">
      <h1>Рестораны</h1>
      {loading ? (
        <p>Загрузка...</p>
      ) : (
        <div className="restaurants-grid">
          {restaurants.map(restaurant => (
            <div key={restaurant.id} className="restaurant-card">
              <img
                src={`http://192.168.1.66:8000/files/location/${restaurant.id}/get-photo`}
                alt={restaurant.name}
                className="restaurant-image"
              />
              <div className="restaurant-info">
                <h2>{restaurant.name}</h2>
                <p>{restaurant.address}</p>
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="pagination">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Назад
        </button>
        <span>Страница {currentPage} из {totalPages}</span>
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
          Вперёд
        </button>
      </div>
    </div>
  );
};

export default App;
