import React, { useState, useEffect, useRef } from 'react';
import { ChevronLeft, ChevronRight, Phone, Mail, MapPin, X, LogIn } from 'lucide-react';
import './css/home2.css';
import logoImage from './9Asset_1_4x.png';
import mainImage1 from './nikolskaya.jpg';
import mainImage2 from './prospekt_mira.jpg';
import mainImage3 from './strast.jpg';
import gallery11 from './image/galp1.jpg';
import gallery12 from './image/galp2.jpg';
import gallery13 from './image/galp3.jpg';
import gallery14 from './image/galp4.jpg';
import gallery21 from './image/galn1.jpg';
import gallery22 from './image/galn2.jpg';
import gallery23 from './image/galn3.jpg';
import gallery24 from './image/galn4.jpg';
import gallery31 from './image/gals1.JPG';
import gallery32 from './image/gals2.jpg';
import gallery33 from './image/gals3.JPG';
import gallery34 from './image/gals4.jpg';

const restaurants = [
  {
    id: 1,
    name: 'Проспект мира',
    description: 'Уютный ресторан в самом сердце города с изысканной атмосферой и великолепным видом.',
    mainImage: mainImage2,
    gallery: [gallery11, gallery12, gallery13, gallery14],
  },
  {
    id: 2,
    name: 'Никольская',
    description: 'Современное пространство с авторской кухней и профессиональным обслуживанием.',
    mainImage: mainImage1,
    gallery: [gallery21, gallery22, gallery23, gallery24],
  },
  {
    id: 3,
    name: 'Страстной бульвар',
    description: 'Элегантный ресторан с богатой историей и непревзойденным сервисом.',
    mainImage: mainImage3,
    gallery: [gallery31, gallery32, gallery33, gallery34],
  },
];

const App = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [currentGalleryImage, setCurrentGalleryImage] = useState(0);
  const carouselRef = useRef(null);
  const contactRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('home-visible');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (carouselRef.current) observer.observe(carouselRef.current);
    if (contactRef.current) observer.observe(contactRef.current);

    return () => observer.disconnect();
  }, []);

  const nextSlide = () => setCurrentSlide((prev) => (prev + 1) % restaurants.length);
  const prevSlide = () => setCurrentSlide((prev) => (prev - 1 + restaurants.length) % restaurants.length);

  const openModal = (restaurant) => {
    setSelectedRestaurant(restaurant);
    setCurrentGalleryImage(0);
  };

  const closeModal = () => setSelectedRestaurant(null);

  const nextGalleryImage = () =>
    setCurrentGalleryImage((prev) => (prev + 1) % (selectedRestaurant?.gallery.length || 0));

  const prevGalleryImage = () =>
    setCurrentGalleryImage((prev) => (prev - 1 + (selectedRestaurant?.gallery.length || 0)) % (selectedRestaurant?.gallery.length || 0));

  return (
    <div>
      <a href="/login" className="home-login-button">
        <LogIn />
        Личный кабинет
      </a>

      <section className="home-hero">
        <div className="home-hero-content">
          <div className="home-logo-container">
            <img src={logoImage} alt="HookahPlace Logo" className="home-logo" />
          </div>
          <h1 className="home-hero-title">
            <span className="home-hookah">Hookah</span> Place <span className="home-employers">Employers</span>
          </h1>
        </div>
      </section>

      <section className="home-carousel-section" ref={carouselRef}>
        <div className="home-carousel-container">
          <button className="home-carousel-button home-prev" onClick={prevSlide}>
            <ChevronLeft />
          </button>
          <div className="home-carousel">
            {restaurants.map((restaurant, index) => (
              <div
                key={restaurant.id}
                className={`home-restaurant-card ${index === currentSlide ? 'home-current' : ''}`}
                onClick={() => openModal(restaurant)}
              >
                <img
                  src={restaurant.mainImage}
                  alt={restaurant.name}
                  className="home-restaurant-image"
                />
                <div className="home-restaurant-info">
                  <h3 className="home-restaurant-name">{restaurant.name}</h3>
                </div>
              </div>
            ))}
          </div>
          <button className="home-carousel-button home-next" onClick={nextSlide}>
            <ChevronRight />
          </button>
        </div>
      </section>

      <section className="home-contact-section" ref={contactRef}>
        <div className="home-contact-container">
          <h2 className="home-contact-title">Контакты</h2>
          <div className="home-contact-grid">
            <div className="home-contact-item">
              <Phone />
              <p>+7 (495) 000-00-00</p>
            </div>
            <div className="home-contact-item">
              <Mail />
              <p>info@hookahplace.com</p>
            </div>
            <div className="home-contact-item">
              <MapPin />
              <p>Москва, улица Примерная, д. 1</p>
            </div>
          </div>
        </div>
      </section>

      {selectedRestaurant && (
        <div className={`home-modal ${selectedRestaurant ? 'home-open' : ''}`} onClick={closeModal}>
          <div className="home-modal-content" onClick={(e) => e.stopPropagation()}>
            <button className="home-modal-close" onClick={closeModal}>
              <X />
            </button>
            <div className="home-modal-gallery">
              <button className="home-modal-gallery-button home-prev" onClick={prevGalleryImage}>
                <ChevronLeft />
              </button>
              <img
                src={selectedRestaurant.gallery[currentGalleryImage]}
                alt="gallery"
                className="home-modal-gallery-image"
              />
              <button className="home-modal-gallery-button home-next" onClick={nextGalleryImage}>
                <ChevronRight />
              </button>
            </div>
            <div className="home-modal-info">
              <h3>{selectedRestaurant.name}</h3>
              <p>{selectedRestaurant.description}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
