import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import './css/Home.css';
import './css/table.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [workDays, setWorkDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isDescriptionOpen, setIsDescriptionOpen] = useState(false); // состояние для описания

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Нет токена авторизации');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://192.168.1.66:8000/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`Ошибка загрузки профиля: ${response.status}`);
        }

        const data = await response.json();
        setProfile(data.user);
        setWorkDays(data.work_days);

        const userId = data.user.id;
        const photoUrl = userId ? `http://192.168.1.66:8000/files/employer/${userId}/get-photo` : null;

        if (photoUrl) {
          const photoResponse = await fetch(photoUrl, {
            method: 'GET',
            headers: { 'Authorization': `Bearer ${token}` },
          });

          if (photoResponse.ok) {
            const photoData = await photoResponse.json();
            if (photoData.status === 'success' && photoData.file_url) {
              setPhotoUrl(photoData.file_url.url);
            }
          }
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const toggleDescription = () => {
    setIsDescriptionOpen((prev) => !prev); // переключение состояния описания
  };

  return (
    <div className="container">
      <div className="main-content">
        <Header title="Личный кабинет" subtitle="Ваши данные" />

        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : profile ? (
          <>
            <div className="profile-header">
              <div className="profile-photo">
                <img id="test-image" src={photoUrl || ''} alt="Фото пользователя" />
              </div>
              <div className="profile-info">
                <h1 className="profile-name">{profile.fio}</h1>
              </div>
            </div>

            <div className="profile-details">
              <div className="profile-job">
                <p>Должность: <span>{profile.work_type}</span></p>
              </div>
              <div className="personal-data">
                <h2>Личные данные</h2>
                <ul>
                  <li><strong>Email:</strong> {profile.email}</li>
                  <li><strong>Контакты:</strong> {profile.contacts?.join(', ') || 'Нет данных'}</li>
                </ul>

                {/* Блок с описанием */}
                <div className="description-block">
                  <div
                    className="description-header"
                    onClick={toggleDescription} // при клике вызываем функцию переключения
                  >
                    <h3>Описание</h3>
                    <span className={`toggle-icon ${isDescriptionOpen ? 'open' : 'closed'}`}>
                      {isDescriptionOpen ? '-' : '+'}
                    </span>
                  </div>

                  <div className={`description-content ${isDescriptionOpen ? 'open' : 'closed'}`}>
                    <p>{profile.description || 'Нет описания'}</p>
                  </div>
                </div>
              </div>
            </div>

            <div className="schedule-container" style={{ maxHeight: '400px', overflowY: 'auto' }}>
              {workDays.length === 0 ? (
                <div className="no-data">Нет данных для отображения</div>
              ) : (
                <div className="employees-list" style={{ display: 'block' }}>
                  {workDays.map((workday, index) => (
                    <div key={index} className="employee-card" style={{ marginBottom: '15px' }}>
                      <div className="card-content">
                        <h3 className="employee-name">{workday.employer_fio}</h3>
                        <p className="employee-date">{new Date(workday.work_time).toLocaleDateString()}</p>
                        <span className={`status-badge ${workday.status === 1 ? 'status-working' : 'status-absent'}`}>
                          {workday.status === 1 ? 'Работал' : 'Отсутствовал'}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </>
        ) : (
          <p>Нет данных</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
