import './css/profile.css';

function App() {
  const [employeeData, setEmployeeData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Нет токена авторизации');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://172.20.10.2:8000/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`Ошибка загрузки профиля: ${response.status}`);
        }

        const data = await response.json();
        const userData = {
          name: data.user.fio,
          position: data.user.work_type,
          photo: data.user.photo_url || '', // URL фото пользователя
          description: data.user.description || 'Нет описания',
          contacts: [
            { type: 'Email', value: data.user.email },
            { type: 'Телефон', value: data.user.contacts?.phone || 'Нет данных' },
            { type: 'Skype', value: data.user.contacts?.skype || 'Нет данных' },
          ],
          schedule: data.work_days.map(workday => ({
            date: new Date(workday.work_time).toLocaleDateString('ru-RU'),
            time: workday.work_time.split('T')[1] || 'Нет данных',
          })),
        };

        setEmployeeData(userData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEmployeeData();
  }, []);

  if (loading) {
    return <div className="profile-container">Загрузка...</div>;
  }

  if (error) {
    return <div className="profile-container error">{error}</div>;
  }

  if (!employeeData) {
    return <div className="profile-container">Нет данных для отображения</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img 
          src={employeeData.photo} 
          alt={employeeData.name} 
          className="profile-image"
        />
        <div className="profile-info">
          <h1 className="profile-name">{employeeData.name}</h1>
          <h2 className="profile-position">{employeeData.position}</h2>

          <div className="info-card">
            <h3 className="info-card-title">О сотруднике</h3>
            <p className="profile-description">{employeeData.description}</p>
          </div>

          <div className="info-card">
            <h3 className="info-card-title">Контактная информация</h3>
            <ul className="profile-contacts">
              {employeeData.contacts.map((contact, index) => (
                <li key={index} className="contact-item">
                  <span className="contact-label">{contact.type}:</span>
                  <span className="contact-value">{contact.value}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <section className="schedule-section">
        <h2 className="schedule-title">Расписание</h2>
        <div className="schedule-cards">
          {employeeData.schedule.map((item, index) => (
            <div key={index} className="schedule-card">
              <div className="card-date">{item.date}</div>
              <div className="card-time">{item.time}</div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default App;
