import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom'; // useNavigate импортирован только один раз
import Header from '../components/Header';
import SearchBar from '../components/SearchBar';
import './css/card_res.css'; // Стили для карточек


const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [restaurantData, setRestaurantData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [lastEvent, setLastEvent] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchRestaurants = async () => {
      try {
        console.log(`Fetching data: page=${page}&limit=${limit}`);
        const response = await fetch(`http://192.168.1.66:8000/locations/get_locations?page=${page}&limit=${limit}`);

        if (!response.ok) throw new Error(`Ошибка: ${response.statusText}`);

        const data = await response.json();
        console.log('Data received:', data);

        const restaurantsWithPhotos = await Promise.all(
          data.map(async (restaurant) => {
            const photoUrl = await fetchPhotoUrl(restaurant.id);
            return { ...restaurant, photoUrl };
          })
        );

        setRestaurantData(restaurantsWithPhotos);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError(`Ошибка запроса: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    const fetchLastEvent = async () => {
      try {
        const response = await fetch('http://192.168.1.66:8000/events/get_latest');

        if (!response.ok) throw new Error(`Ошибка: ${response.statusText}`);

        const eventData = await response.json();
        setLastEvent(eventData);
      } catch (err) {
        console.error('Error fetching last event:', err);
      }
    };

    fetchRestaurants();
    fetchLastEvent();
  }, [page]);

  const fetchPhotoUrl = async (id) => {
    try {
      const response = await fetch(`http://192.168.1.66:8000/files/location/${id}/get-photo`);

      if (!response.ok) throw new Error(`Ошибка: ${response.statusText}`);

      const photoData = await response.json();
      return photoData.status === 'success' && photoData.file_url ? photoData.file_url.url : null;
    } catch (err) {
      console.error('Error fetching photo:', err);
      return null;
    }
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>Ошибка: {error}</p>;

  return (
    <div className="main-content">
      <Header title="HookahPlace Futura" />

      <section className="content">
        <h2>Список ресторанов</h2>

        {/* Поле поиска */}
        <div className="search-container">
          <SearchBar onSearch={setSearchTerm} />
        </div>

        {/* Карточки ресторанов */}
        <div className="card-container">
          {restaurantData
            .filter(row => row.name.toLowerCase().includes(searchTerm.toLowerCase()))
            .map((restaurant, index) => (
              <div key={index} className="card">
                <div className="card-photo">
                  <img 
                    src={restaurant.photoUrl || 'default-photo-url.jpg'} 
                    alt={`Фото ${restaurant.name}`} 
                    className="restaurant-photo" 
                  />
                </div>
                <div className="card-info">
                  <h3>{restaurant.name}</h3>
                  <p><strong>Адрес:</strong> {restaurant.address}</p>
                  <p><strong>Описание:</strong> {restaurant.description}</p>
                </div>
              </div>
            ))}
        </div>

        {/* Карточка с последним событием */}
        {lastEvent && (
          <section className="event-card">
            <div className="event-card-inner">
              <h2>Последнее событие</h2>
              <div className="event-details">
                <h3>{lastEvent.name}</h3>
                <p><strong>Дата:</strong> {new Date(lastEvent.date_start).toLocaleDateString()}</p>
                <p>{lastEvent.description}</p>
              </div>
              <button className="event-details-button" onClick={() => navigate(`/event/${lastEvent.id}`)}>
                Подробнее
              </button>
            </div>
          </section>
        )}

        {/* Кнопки для пагинации */}
        <div className="pagination">
          <button onClick={() => setPage(prev => Math.max(prev - 1, 1))} disabled={page <= 1}>
            Назад
          </button>
          <button onClick={() => setPage(prev => prev + 1)}>
            Вперед
          </button>
        </div>
      </section>
    </div>
  );
};

export default Home;
