import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { FaEdit, FaTimes, FaPlus, FaUser, FaCalendar, FaArrowLeft } from 'react-icons/fa';
import './css/admin.css';

const BASE_URL = 'https://mdmvbistdt.eu.loclx.io';
const LOCATIONS = ['Арбат', 'Проспект мира', 'Никольская'];
const ROLES = ['Повар', 'Кальянщик', 'Бармен', 'Официант', 'Хостес', 'Другое'];

function App() {
  const [currentView, setCurrentView] = useState('main');
  const [selectedType, setSelectedType] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [modalType, setModalType] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    if (selectedType && selectedLocation) fetchItems();
  }, [selectedType, selectedLocation, page]);

  const fetchItems = async () => {
    setLoading(true);
    try {
      const endpoint = {
        workers: `/employers/get_list_employers/${selectedLocation}`,
        regulars: `/residents/${selectedLocation}/get_list_residents`,
        events: `/events/get_not_actually_events/${selectedLocation}`,
      }[selectedType];
      const response = await axios.get(`${BASE_URL}${endpoint}`, {
        params: { page, limit },
      });
      setItems(response.data.items);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    const endpoint = {
      workers: '/employers/delete_employer',
      regulars: `/residents/${selectedLocation}/delete_resident`,
      events: '/events/delete_event',
    }[selectedType];
    try {
      await axios.delete(`${BASE_URL}${endpoint}`, { data: { id } });
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  const handleAddEdit = async (formData) => {
    const endpoint = {
      workers: '/employers/edit_employer',
      regulars: '/residents/create_resident',
      events: '/events/create_event',
    }[selectedType];
    try {
      if (modalType === 'add') {
        await axios.post(`${BASE_URL}${endpoint}`, formData);
      } else {
        await axios.put(`${BASE_URL}${endpoint}`, formData);
      }
      fetchItems();
    } catch (error) {
      console.error('Error adding/editing item:', error);
    } finally {
      setShowModal(false);
    }
  };

  const handleMainButtonClick = (type) => {
    setSelectedType(type);
    setCurrentView('locations');
  };

  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
    setPage(1);
    setCurrentView('items');
  };

  const renderMainView = () => (
    <div className="main-buttons">
      <button className="main-button" onClick={() => handleMainButtonClick('workers')}>
        <FaUser /> Работники
      </button>
      <button className="main-button" onClick={() => handleMainButtonClick('regulars')}>
        <FaUser /> Постоянники
      </button>
      <button className="main-button" onClick={() => handleMainButtonClick('events')}>
        <FaCalendar /> События
      </button>
    </div>
  );

  const renderLocationsView = () => (
    <div className="locations">
      {LOCATIONS.map((location) => (
        <div key={location} className="location-card" onClick={() => handleLocationSelect(location)}>
          <h3>{location}</h3>
        </div>
      ))}
    </div>
  );

  const renderItemsView = () => (
    <div className="items-view">
      <div className="items-list">
        {loading ? (
          <p>Загрузка...</p>
        ) : (
          items.map((item) => (
            <div key={item.id} className="item-row">
              <span>{item.name || item.title}</span>
              <div className="actions">
                <button onClick={() => { setModalType('edit'); setShowModal(true); setSelectedItem(item); }}>Редактировать</button>
                <button onClick={() => handleDelete(item.id)}>Удалить</button>
              </div>
            </div>
          ))
        )}
      </div>
      <div className="pagination">
        <button disabled={page <= 1} onClick={() => setPage((prev) => prev - 1)}>Назад</button>
        <span>{page} / {totalPages}</span>
        <button disabled={page >= totalPages} onClick={() => setPage((prev) => prev + 1)}>Вперед</button>
      </div>
      <button onClick={() => { setModalType('add'); setShowModal(true); setSelectedItem(null); }}>Добавить</button>
    </div>
  );

  return (
    <div className="admin-panel">
      {currentView === 'main' && renderMainView()}
      {currentView === 'locations' && renderLocationsView()}
      {currentView === 'items' && renderItemsView()}
      {showModal && (
        <ModalForm
          type={selectedType}
          data={selectedItem}
          onSubmit={handleAddEdit}
          onCancel={() => setShowModal(false)}
        />
      )}
    </div>
  );
}

function ModalForm({ type, data, onSubmit, onCancel }) {
  const [formData, setFormData] = useState(data || {});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit}>
        <h3>{data ? 'Редактировать' : 'Добавить'} {type}</h3>
        {type === 'events' && (
          <>
            <input name="title" placeholder="Название" value={formData.title || ''} onChange={handleChange} required />
            <input name="date" type="date" value={formData.date || ''} onChange={handleChange} required />
            <input name="time" placeholder="Время" value={formData.time || ''} onChange={handleChange} required />
          </>
        )}
        {type !== 'events' && (
          <>
            <input name="name" placeholder="Имя" value={formData.name || ''} onChange={handleChange} required />
            {type === 'workers' && (
              <>
                <select name="role" value={formData.role || ''} onChange={handleChange} required>
                  <option value="">Выберите роль</option>
                  {ROLES.map((role) => (
                    <option key={role} value={role}>{role}</option>
                  ))}
                </select>
                <input name="email" placeholder="Email" value={formData.email || ''} onChange={handleChange} required />
                <input name="phone" placeholder="Телефон" value={formData.phone || ''} onChange={handleChange} required />
              </>
            )}
          </>
        )}
        <textarea name="description" placeholder="Описание" value={formData.description || ''} onChange={handleChange} required />
        <div className="modal-actions">
          <button type="button" onClick={onCancel}>Отмена</button>
          <button type="submit">Сохранить</button>
        </div>
      </form>
    </div>
  );
}

export default App;
