import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import './css/Home.css';
import './css/table.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [workDays, setWorkDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isDescriptionOpen, setIsDescriptionOpen] = useState(false);

  // Пагинация
  const [currentPage, setCurrentPage] = useState(1);
  const [totalItems, setTotalItems] = useState(0);
  const [searchName, setSearchName] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const itemsPerPage = 10;

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Нет токена авторизации');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://192.168.1.66:8000/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`Ошибка загрузки профиля: ${response.status}`);
        }

        const data = await response.json();
        setProfile(data.user);
        setWorkDays(data.work_days);
        setTotalItems(data.work_days.length);

        const userId = data.user.id;
        const photoUrl = userId ? `http://192.168.1.66:8000/files/employer/${userId}/get-photo` : null;

        if (photoUrl) {
          const photoResponse = await fetch(photoUrl, {
            method: 'GET',
            headers: { 'Authorization': `Bearer ${token}` },
          });

          if (photoResponse.ok) {
            const photoData = await photoResponse.json();
            if (photoData.status === 'success' && photoData.file_url) {
              setPhotoUrl(photoData.file_url.url);
            }
          }
        }
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const filteredWorkDays = workDays.filter(day => {
    const nameMatch = day.employer_fio.toLowerCase().includes(searchName.toLowerCase());
    const dateMatch = !selectedDate || new Date(day.work_time).toLocaleDateString() === new Date(selectedDate).toLocaleDateString();
    return nameMatch && dateMatch;
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentWorkDays = filteredWorkDays.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  const getPageNumbers = () => {
    const pageNumbers = [];
    const maxVisiblePages = 5;

    if (totalPages <= maxVisiblePages) {
      for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
      }
    } else {
      pageNumbers.push(1);

      if (currentPage > 3) {
        pageNumbers.push('...');
      }

      for (let i = Math.max(2, currentPage - 1); i <= Math.min(totalPages - 1, currentPage + 1); i++) {
        pageNumbers.push(i);
      }

      if (currentPage < totalPages - 2) {
        pageNumbers.push('...');
      }

      pageNumbers.push(totalPages);
    }

    return pageNumbers;
  };

  return (
    <div className="container">
      <div className="main-content">
        <Header title="Личный кабинет" subtitle="Ваши данные" />

        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : profile ? (
          <>
            <div className="profile-header">
              <div className="profile-photo">
                <img id="test-image" src={photoUrl || ''} alt="Фото пользователя" />
              </div>
              <div className="profile-info">
                <h1 className="profile-name">{profile.fio}</h1>
              </div>
            </div>

            <div className="profile-details">
              <div className="profile-job">
                <p>Должность: <span>{profile.work_type}</span></p>
              </div>
              <div className="personal-data">
                <h2>Личные данные</h2>
                <ul>
                  <li><strong>Email:</strong> {profile.email}</li>
                  <li><strong>Контакты:</strong> {profile.contacts?.join(', ') || 'Нет данных'}</li>
                </ul>
              </div>
            </div>

            {profile.description && (
              <div className="profile-description">
                <button 
                  className="toggle-description"
                  onClick={() => setIsDescriptionOpen(!isDescriptionOpen)}
                >
                  {isDescriptionOpen ? 'Скрыть описание' : 'Показать описание'}
                </button>
                {isDescriptionOpen && <p className="description-text">{profile.description}</p>}
              </div>
            )}

            <div className="schedule-container">
              <div className="filters">
                <div className="filters-grid">
                  <div className="filter-group">
                    <label>Поиск по ФИО</label>
                    <input
                      type="text"
                      value={searchName}
                      onChange={(e) => setSearchName(e.target.value)}
                      placeholder="Введите ФИО сотрудника"
                    />
                  </div>

                  <div className="filter-group">
                    <label>Фильтр по дате</label>
                    <input
                      type="date"
                      value={selectedDate}
                      onChange={(e) => setSelectedDate(e.target.value)}
                    />
                  </div>
                </div>
              </div>

              {filteredWorkDays.length === 0 ? (
                <div className="no-data">Нет данных для отображения</div>
              ) : (
                <div className="employees-grid">
                  {currentWorkDays.map((workday, index) => (
                    <div key={index} className="employee-card">
                      <div className="card-content">
                        <h3 className="employee-name">{workday.employer_fio}</h3>
                        <p className="employee-date">{new Date(workday.work_time).toLocaleDateString()}</p>
                        <span className={`status-badge ${workday.status === 1 ? 'status-working' : 'status-absent'}`}>
                          {workday.status === 1 ? 'Работал' : 'Отсутствовал'}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              {totalPages > 1 && (
                <div className="pagination">
                  <button
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                  >
                    ←
                  </button>

                  {getPageNumbers().map((pageNum, index) => (
                    pageNum === '...' ? (
                      <span key={`ellipsis-${index}`} className="ellipsis">...</span>
                    ) : (
                      <button
                        key={pageNum}
                        onClick={() => setCurrentPage(pageNum)}
                        className={currentPage === pageNum ? 'active' : ''}
                      >
                        {pageNum}
                      </button>
                    )
                  ))}

                  <button
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                  >
                    →
                  </button>
                </div>
              )}

              <div className="results-counter">
                Показано {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredWorkDays.length)} из {totalItems} сотрудников
              </div>
            </div>
          </>
        ) : (
          <p>Нет данных</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
