import React from 'react';

export const Profile = ({ onBack }) => {
  // Get dates for the next 30 days
  const dates = Array.from({ length: 30 }, (_, i) => {
    const date = new Date();
    date.setDate(date.getDate() + i);
    return date;
  });

  // Function to get schedule status
  const getScheduleStatus = (date) => {
    const dayName = date.toLocaleDateString('ru-RU', { weekday: 'long' });
    const schedule = currentEmployee.schedule.find(s => 
      s.day.toLowerCase() === dayName.toLowerCase()
    );

    if (!schedule) return 'не начал';
    return 'работает';
  };

  return (
    <div className="container">
      <button onClick={onBack} className="back-button">
        <span>Назад</span>
      </button>

      <div className="profile-card">
        <div className="profile-header">
          <img
            className="profile-image"
            src={currentEmployee.imageUrl}
            alt={currentEmployee.fullName}
          />
          <div className="profile-info">
            <div className="profile-position">
              {currentEmployee.position}
            </div>
            <h1 className="profile-name">
              {currentEmployee.fullName}
            </h1>
            <p className="profile-description">
              {currentEmployee.description}
            </p>
          </div>
        </div>

        <div className="schedule-section">
          <div className="schedule-list">
            {dates.map((date) => {
              const status = getScheduleStatus(date);

              return (
                <div key={date.toISOString()} className="schedule-card">
                  <div className="schedule-card-content">
                    <h3 className="schedule-name">{currentEmployee.fullName}</h3>
                    <div className="schedule-date">
                      {date.toLocaleDateString('ru-RU', { 
                        day: '2-digit',
                        month: '2-digit',
                        year: 'numeric'
                      })}
                    </div>
                    <div className={`schedule-status ${status === 'работает' ? 'working' : 'not-working'}`}>
                      {status}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="schedule-footer">
            Показано 1-3 из 3 сотрудников
          </div>
        </div>
      </div>
    </div>
  );
};
