import React, { useState, useEffect } from 'react';
import './css/Home.css';

const limit = 5;  // или любое другое значение для лимита

function App() {
  const [restaurantsData, setRestaurantsData] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);

  const formatDate = (dateStr) => {
    return new Date(dateStr).toLocaleDateString('ru-RU');
  };

  useEffect(() => {
    // Запрос к API для получения данных
    fetch(`http://192.168.1.66:8000/locations/get_locations?limit=${limit}`)
      .then(response => response.json())
      .then(data => {
        setRestaurantsData(data); // Сохраняем данные о ресторанах
      })
      .catch(error => {
        console.error("Ошибка при получении данных: ", error);
      });
  }, []); // Запрос выполняется один раз при монтировании компонента

  return (
    <div className="container">
      <h1>Рестораны</h1>

      <div className="restaurants-grid">
        {restaurantsData.map(restaurant => (
          <div 
            key={restaurant.id} 
            className="restaurant-card"
            onClick={() => setSelectedRestaurant(restaurant)}
          >
            <img 
              src={`http://192.168.1.66:8000/files/location/${restaurant.id}/get-photo`} 
              alt={restaurant.name} 
              className="restaurant-image"
            />
            <div className="restaurant-info">
              <h2>{restaurant.name}</h2>
              <p>{restaurant.address}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedRestaurant && (
        <div className="modal-overlay" onClick={() => setSelectedRestaurant(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button 
              className="close-button"
              onClick={() => setSelectedRestaurant(null)}
            >
              ×
            </button>

            <img 
              src={`http://192.168.1.66:8000/files/location/${selectedRestaurant.id}/get-photo`} 
              alt={selectedRestaurant.name} 
              className="modal-image"
            />

            <div className="modal-info">
              <h2>{selectedRestaurant.name}</h2>
              <p className="address">{selectedRestaurant.address}</p>
              <p className="description">{selectedRestaurant.description}</p>

              <div className="event-section">
                <h3>Последнее событие</h3>
                <div className="event-card">
                  <h4>{selectedRestaurant.lastEvent?.title}</h4>
                  <p className="event-date">
                    {formatDate(selectedRestaurant.lastEvent?.startDate)}, {selectedRestaurant.lastEvent?.startTime}
                  </p>
                  <p className="event-description">
                    {selectedRestaurant.lastEvent?.shortDescription}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
