import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import './css/Home.css';
import './css/table.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [workDays, setWorkDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isDescriptionVisible, setIsDescriptionVisible] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Нет токена авторизации');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://192.168.1.66:8000/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) throw new Error(`Ошибка загрузки профиля: ${response.status}`);

        const data = await response.json();
        setProfile(data.user);
        setWorkDays(data.work_days || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const toggleDescriptionVisibility = () => {
    setIsDescriptionVisible((prev) => !prev);
  };

  return (
    <div className="container">
      <div className="main-content">
        <Header title="Личный кабинет" subtitle="Ваши данные" />

        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : profile ? (
          <>
            <div className="profile-header">
              <div className="profile-photo">
                {profile.id ? (
                  <img
                    src={`http://192.168.1.66:8000/files/employer/${profile.id}/get-photo`}
                    alt="Фото пользователя"
                  />
                ) : (
                  <p>Нет фото</p>
                )}
              </div>
              <div className="profile-info">
                <h1 className="profile-name">{profile.fio}</h1>
              </div>
            </div>

            <div className="profile-details">
              <div className="profile-job">
                <p>Должность: <span>{profile.work_type || 'Не указано'}</span></p>
              </div>
              <div className="personal-data">
                <h2>Личные данные</h2>
                <ul>
                  <li><strong>Email:</strong> {profile.email || 'Не указан'}</li>
                  <li><strong>Контакты:</strong> {profile.contacts?.join(', ') || 'Нет данных'}</li>
                </ul>
              </div>
            </div>

            <div className="space-between-blocks"></div>

            <div className="description-container">
              <div className="description-toggle">
                <button
                  className={`show-description-button ${isDescriptionVisible ? 'rotated' : ''}`}
                  onClick={toggleDescriptionVisibility}
                >
                  {isDescriptionVisible ? '▲' : '▼'}
                </button>
              </div>
              <div className={`description-block ${isDescriptionVisible ? 'visible' : ''}`}>
                <p>{profile.description || 'Нет описания'}</p>
              </div>
            </div>

            <div className="schedule-container">
              {workDays.length === 0 ? (
                <div className="no-data">Нет данных для отображения</div>
              ) : (
                <div className="employees-list">
                  {workDays.map((workday, index) => (
                    <div key={index} className="employee-card">
                      <div className="card-content">
                        <h3 className="employee-name">{workday.employer_fio}</h3>
                        <p className="employee-date">{new Date(workday.work_time).toLocaleDateString()}</p>
                        <span className={`status-badge ${workday.status === 1 ? 'status-working' : 'status-absent'}`}>
                          {workday.status === 1 ? 'Работал' : 'Отсутствовал'}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </>
        ) : (
          <p>Нет данных</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
