import './css/profile.css'

function App() {
  const employeeData = {
    name: 'Иван Петров',
    position: 'Старший разработчик',
    photo: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2340&q=80',
    description: 'Опытный разработчик с более чем 5-летним стажем в создании веб-приложений. Специализируется на React и Node.js. Активно участвует в развитии команды и менторстве junior-разработчиков.',
    contacts: [
      { type: 'Email', value: 'ivan.petrov@company.com' },
      { type: 'Телефон', value: '+7 (999) 123-45-67' },
      { type: 'Skype', value: 'ivan.petrov.dev' }
    ],
    schedule: [
      {
        date: '2024-03-18',
        time: '10:00 - 11:30',
        activity: 'Ежедневный созвон команды'
      },
      {
        date: '2024-03-18',
        time: '13:00 - 15:00',
        activity: 'Работа над проектом А'
      },
      {
        date: '2024-03-18',
        time: '16:00 - 17:30',
        activity: 'Код-ревью'
      },
      {
        date: '2024-03-19',
        time: '11:00 - 12:30',
        activity: 'Встреча с клиентом'
      },
      {
        date: '2024-03-19',
        time: '14:00 - 16:00',
        activity: 'Разработка новых функций'
      }
    ]
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img 
          src={employeeData.photo} 
          alt={employeeData.name} 
          className="profile-image"
        />
        <div className="profile-info">
          <h1 className="profile-name">{employeeData.name}</h1>
          <h2 className="profile-position">{employeeData.position}</h2>

          <div className="info-card">
            <h3 className="info-card-title">О сотруднике</h3>
            <p className="profile-description">{employeeData.description}</p>
          </div>

          <div className="info-card">
            <h3 className="info-card-title">Контактная информация</h3>
            <ul className="profile-contacts">
              {employeeData.contacts.map((contact, index) => (
                <li key={index} className="contact-item">
                  <span className="contact-label">{contact.type}:</span>
                  <span className="contact-value">{contact.value}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <section className="schedule-section">
        <h2 className="schedule-title">Расписание</h2>
        <div className="schedule-cards">
          {employeeData.schedule.map((item, index) => (
            <div key={index} className="schedule-card">
              <div className="card-date">
                {new Date(item.date).toLocaleDateString('ru-RU', {
                  weekday: 'long',
                  year: 'numeric',
                  month: 'long',
                  day: 'numeric'
                })}
              </div>
              <div className="card-time">{item.time}</div>
              <div className="card-activity">{item.activity}</div>
            </div>
          ))}
        </div>
      </section>
    </div>
  )
}

export default App
