import React, { useState, useEffect } from 'react';
import './css/home2.css';

function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [events, setEvents] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(3); // Количество ресторанов на странице
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // Для хранения ошибок

  // Запрос для получения ресторанов
  const fetchRestaurants = async () => {
    try {
      const response = await fetch(`http://192.168.1.66:8000/locations/get_locations?page=${currentPage}&limit=${itemsPerPage}`);
      if (!response.ok) {
        throw new Error('Ошибка при получении ресторанов');
      }
      const data = await response.json();
      setRestaurants(data.locations);  // предполагается, что данные ресторанов лежат в locations
    } catch (error) {
      setError(error.message);
      console.error('Ошибка при получении ресторанов:', error);
    } finally {
      setLoading(false);
    }
  };

  // Запрос для получения событий
  const fetchEvents = async () => {
    try {
      const response = await fetch('http://192.168.1.66:8000/events/get_latest');
      if (!response.ok) {
        throw new Error('Ошибка при получении событий');
      }
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      setError(error.message);
      console.error('Ошибка при получении событий:', error);
    }
  };

  useEffect(() => {
    fetchRestaurants();
    fetchEvents();
  }, [currentPage]);

  // Запрос для получения изображений
  const fetchImage = async (category, fileId) => {
    try {
      const response = await fetch(`http://192.168.1.66:8000/files/${category}/${fileId}/get-photo`);
      return await response.blob();
    } catch (error) {
      setError('Ошибка при загрузке изображения');
      console.error('Ошибка при получении изображения:', error);
    }
  };

  const [imageCache, setImageCache] = useState({});

  // Кэширование изображений
  const getRestaurantImage = (category, fileId) => {
    const key = `${category}-${fileId}`;
    if (imageCache[key]) {
      return imageCache[key];
    }
    fetchImage(category, fileId).then((imageBlob) => {
      setImageCache((prevCache) => ({
        ...prevCache,
        [key]: URL.createObjectURL(imageBlob),
      }));
    });
    return null;
  };

  // Форматирование даты
  const formatDate = (dateStr) => {
    return new Date(dateStr).toLocaleDateString('ru-RU');
  };

  return (
    <div className="container">
      <header>
        <h1>Добро пожаловать в наш ресторанный портал</h1>
      </header>

      {error && (
        <div className="error-message">
          <p>{error}</p>
        </div>
      )}

      <h2>Рестораны</h2>

      <div className="restaurants-grid">
        {loading ? (
          <p>Загружаем данные...</p>
        ) : (
          restaurants.length > 0 ? (
            restaurants.map(restaurant => (
              <div 
                key={restaurant.id} 
                className="restaurant-card"
                onClick={() => handleCardClick(restaurant)}
              >
                {/* Загрузка изображения для ресторана */}
                <img 
                  src={getRestaurantImage(restaurant.category, restaurant.file_id)} 
                  alt={restaurant.name} 
                  className="restaurant-image"
                />
                <div className="restaurant-info">
                  <h2>{restaurant.name}</h2>
                  <p>{restaurant.address}</p>
                </div>
              </div>
            ))
          ) : (
            <p>Нет ресторанов для отображения.</p>
          )
        )}
      </div>

      {events.length > 0 && (
        <div className="latest-events-section">
          <h3>Последние события</h3>
          {events.map(event => (
            <div className="event-card" key={event.id}>
              <h4>{event.name}</h4>
              <p className="event-date">
                {formatDate(event.date_start)}
              </p>
              <p className="event-description">
                {event.description}
              </p>
            </div>
          ))}
        </div>
      )}

      {/* Кнопки для навигации между страницами */}
      <div className="pagination">
        <button 
          disabled={currentPage === 1} 
          onClick={() => setCurrentPage(prev => prev - 1)}
        >
          Назад
        </button>
        <button 
          onClick={() => setCurrentPage(prev => prev + 1)}
        >
          Вперед
        </button>
      </div>
    </div>
  );
}

export default App;
