import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import './css/Home.css';
import './css/table.css';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [photoUrl, setPhotoUrl] = useState(null);
  const [workDays, setWorkDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isDescriptionVisible, setIsDescriptionVisible] = useState(false); // Состояние для видимости блока описания

  useEffect(() => {
  const fetchProfile = async () => {
    setLoading(true);
    setError(null);

    const token = localStorage.getItem('token');
    if (!token) {
      setError('Нет токена авторизации');
      setLoading(false);
      return;
    }

    try {
      const response = await fetch('http://192.168.1.66:8000/auth/profile', {
        headers: { 'Authorization': `Bearer ${token}` },
      });

      if (!response.ok) throw new Error(`Ошибка загрузки профиля: ${response.status}`);

      const data = await response.json();
      setProfile(data.user);
      setWorkDays(data.work_days || []);

      if (data.user?.id) {
        setPhotoUrl(`http://192.168.1.66:8000/files/employer/${data.user.id}/get-photo`);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  fetchProfile();
}, []);


  const toggleDescriptionVisibility = () => {
    setIsDescriptionVisible(prev => !prev); // Переключаем видимость блока
  };

  return (
    <div className="container">
      <div className="main-content">
        <Header title="Личный кабинет" subtitle="Ваши данные" />

        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : profile ? (
          <>
            <div className="profile-header">
              <div className="profile-photo">
                <img id="test-image" src={photoUrl || ''} alt="Фото пользователя" />
              </div>
              <div className="profile-info">
                <h1 className="profile-name">{profile.fio}</h1>
              </div>
            </div>

            <div className="profile-details">
              <div className="profile-job">
                <p>Должность: <span>{profile.work_type}</span></p>
              </div>
              <div className="personal-data">
                <h2>Личные данные</h2>
                <ul>
                  <li><strong>Email:</strong> {profile.email}</li>
                  <li><strong>Контакты:</strong> {profile.contacts?.join(', ') || 'Нет данных'}</li>
                </ul>
              </div>
            </div>

            {/* Пробел между блоками */}
            <div className="space-between-blocks"></div>

            {/* Блок с описанием */}
            <div className="description-container">
              <div className="description-toggle">
                <button 
                  className={`show-description-button ${isDescriptionVisible ? 'rotated' : ''}`} 
                  onClick={toggleDescriptionVisibility}
                >
                  {isDescriptionVisible ? '▲' : '▼'}
                </button>
              </div>
              <div
                className={`description-block ${isDescriptionVisible ? 'visible' : ''}`}
                style={{
                  transition: 'max-height 0.3s ease-in-out, padding 0.3s ease-in-out',
                  maxHeight: isDescriptionVisible ? '300px' : '0',
                  padding: isDescriptionVisible ? '10px' : '0',
                  overflow: 'hidden',
                }}
              >
                <p>{profile.description || 'Нет описания'}</p>
              </div>
            </div>

            <div className="schedule-container" style={{ maxHeight: '400px', overflowY: 'auto' }}>
              {workDays.length === 0 ? (
                <div className="no-data">Нет данных для отображения</div>
              ) : (
                <div className="employees-list" style={{ display: 'block' }}>
                  {workDays.map((workday, index) => (
                    <div key={index} className="employee-card" style={{ marginBottom: '15px' }}>
                      <div className="card-content">
                        <h3 className="employee-name">{workday.employer_fio}</h3>
                        <p className="employee-date">{new Date(workday.work_time).toLocaleDateString()}</p>
                        <span className={`status-badge ${workday.status === 1 ? 'status-working' : 'status-absent'}`}>
                          {workday.status === 1 ? 'Работал' : 'Отсутствовал'}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </>
        ) : (
          <p>Нет данных</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
