import { useState } from 'react'
import styles from './App.module.css'

const restaurants = [
  {
    id: 1,
    name: 'Проспект мира',
    image: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&w=800&q=80&fm=jpg&crop=entropy&cs=tinysrgb&auto=format&fit=crop&h=400&w=800',
    description: 'Современное пространство с панорамными видами'
  },
  {
    id: 2,
    name: 'Никольская',
    image: 'https://images.unsplash.com/photo-1514933651103-005eec06c04b?q=80&w=2074&auto=format&fit=crop&ixlib=rb-4.0.3&w=800&q=80&fm=jpg&crop=entropy&cs=tinysrgb&auto=format&fit=crop&h=400&w=800',
    description: 'Историческое место с особой атмосферой'
  },
  {
    id: 3,
    name: 'Страстной бульвар',
    image: 'https://images.unsplash.com/photo-1559339352-11d035aa65de?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&w=800&q=80&fm=jpg&crop=entropy&cs=tinysrgb&auto=format&fit=crop&h=400&w=800',
    description: 'Уютный ресторан в центре города'
  }
]

const events = [
  {
    id: 1,
    title: 'Дегустация вин',
    date: '2024-03-20',
    location: 'Проспект мира',
    description: 'Вечер итальянских вин с сомелье международного класса. В программе дегустация 6 видов вин из разных регионов Италии, лекция об особенностях производства и хранения вин.',
    time: '19:00 - 22:00'
  },
  {
    id: 2,
    title: 'Мастер-класс от шефа',
    date: '2024-03-22',
    location: 'Никольская',
    description: 'Готовим настоящую итальянскую пасту с шеф-поваром ресторана. Вы научитесь готовить тесто для пасты, соусы и узнаете секреты правильной подачи блюд.',
    time: '15:00 - 18:00'
  },
  {
    id: 3,
    title: 'Джазовый вечер',
    date: '2024-03-25',
    location: 'Страстной бульвар',
    description: 'Живая джазовая музыка в исполнении квартета профессиональных музыкантов. Специальное меню от шеф-повара, авторские коктейли.',
    time: '20:00 - 23:00'
  }
]

function App() {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null)
  const [selectedLocation, setSelectedLocation] = useState('all')
  const [selectedDate, setSelectedDate] = useState('')
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedEvent, setSelectedEvent] = useState(null)

  if (!selectedRestaurant) {
    return (
      <div className={styles['restaurant-selection']}>
        <h1>Выберите локацию</h1>
        <div className={styles['restaurant-grid']}>
          {restaurants.map(restaurant => (
            <div 
              key={restaurant.id} 
              className={styles['restaurant-card']}
              onClick={() => setSelectedRestaurant(restaurant)}
            >
              <div className={styles['restaurant-image']}>
                <img src={restaurant.image} alt={restaurant.name} />
              </div>
              <div className={styles['restaurant-info']}>
                <h2>{restaurant.name}</h2>
                <p>{restaurant.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    )
  }

  const filteredEvents = events.filter(event => {
    const locationMatch = selectedLocation === 'all' || event.location === selectedLocation
    const dateMatch = !selectedDate || event.date === selectedDate
    const searchMatch = !searchQuery || event.title.toLowerCase().includes(searchQuery.toLowerCase())
    return locationMatch && dateMatch && searchMatch
  })

  return (
    <div className={styles['events-page']}>
      <header className={styles.header}>
        <button className={styles['back-button']} onClick={() => setSelectedRestaurant(null)}>
          ← Назад к выбору ресторана
        </button>
        <div className={styles.filters}>
          <input 
            type="text" 
            placeholder="Поиск по названию"
            className={styles['search-input']}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <input 
            type="date" 
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className={styles['date-input']}
          />
          <select 
            value={selectedLocation} 
            onChange={(e) => setSelectedLocation(e.target.value)}
            className={styles['location-select']}
          >
            <option value="all">Все локации</option>
            <option value="Проспект мира">Проспект мира</option>
            <option value="Никольская">Никольская</option>
            <option value="Страстной бульвар">Страстной бульвар</option>
          </select>
        </div>
      </header>

      <div className={styles['events-list']}>
        {filteredEvents.map(event => (
          <div 
            key={event.id} 
            className={styles['event-card']}
            onClick={() => setSelectedEvent(event)}
          >
            <div className={styles['event-card-content']}>
              <h3>{event.title}</h3>
              <div className={styles['event-details']}>
                <span className={styles['event-location']}>{event.location}</span>
                <span className={styles['event-time']}>{event.time}</span>
              </div>
              <div className={styles['event-date']}>{event.date}</div>
            </div>
          </div>
        ))}
      </div>

      {selectedEvent && (
        <div className={styles['modal-overlay']} onClick={() => setSelectedEvent(null)}>
          <div className={styles['modal-content']} onClick={e => e.stopPropagation()}>
            <button className={styles['close-button']} onClick={() => setSelectedEvent(null)}>×</button>
            <h2>{selectedEvent.title}</h2>
            <p className={styles['modal-location']}>Локация: {selectedEvent.location}</p>
            <p className={styles['modal-datetime']}>
              {selectedEvent.date} | {selectedEvent.time}
            </p>
            <p className={styles['modal-description']}>{selectedEvent.description}</p>
          </div>
        </div>
      )}
    </div>
  )
}

export default App
