import { useState, useEffect } from 'react';
import './css/table.css';
import React from 'react';

function App() {
  const [employees, setEmployees] = useState([]);  // Храним данные сотрудников
  const [currentPage, setCurrentPage] = useState(1);
  const [searchName, setSearchName] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const itemsPerPage = 10;

  // Функция для получения данных с сервера
  useEffect(() => {
    fetch('http://192.168.1.66:8000/workdays/get_list_workdays')
      .then(response => response.json())
      .then(data => {
        const formattedData = data.map(item => ({
          id: item.id,
          name: item.employer_fio,
          date: item.work_time,  // Предположим, что work_time приходит в нужном формате
          status: getStatusText(item.status)  // Преобразуем статус в строку
        }));
        setEmployees(formattedData);
      })
      .catch(error => console.error('Ошибка при загрузке данных:', error));
  }, []);

  // Преобразуем статус в строку
  const getStatusText = (status) => {
    switch (status) {
      case 1: return 'работает';
      case 2: return 'не начал';
      case 3: return 'не пришел';
      default: return '';
    }
  };

  const filteredEmployees = employees.filter(employee => {
    const nameMatch = employee.name.toLowerCase().includes(searchName.toLowerCase());
    const dateMatch = !selectedDate || employee.date.startsWith(selectedDate);
    return nameMatch && dateMatch;
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentEmployees = filteredEmployees.slice(indexOfFirstItem, indexOfLastItem);
  const totalPages = Math.ceil(filteredEmployees.length / itemsPerPage);

  const getStatusClass = (status) => {
    switch (status) {
      case 'работает': 
        return 'status-working';
      case 'не начал': 
        return 'status-not-started';
      case 'не пришел': 
        return 'status-absent';
      default: 
        return '';
    }
  };

  const getPageNumbers = () => {
    const pageNumbers = [];
    const maxVisiblePages = 5;

    if (totalPages <= maxVisiblePages) {
      for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
      }
    } else {
      pageNumbers.push(1);

      if (currentPage > 3) {
        pageNumbers.push('...');
      }

      for (let i = Math.max(2, currentPage - 1); i <= Math.min(totalPages - 1, currentPage + 1); i++) {
        pageNumbers.push(i);
      }

      if (currentPage < totalPages - 2) {
        pageNumbers.push('...');
      }

      pageNumbers.push(totalPages);
    }

    return pageNumbers;
  };

  return (
    <div className="schedule-container">
      <div className="header">
        <h1>Расписание сотрудников</h1>
        <p>Управление расписанием и статусами сотрудников</p>
      </div>

      <div className="filters">
        <div className="filters-grid">
          <div className="filter-group">
            <label>Поиск по ФИО</label>
            <input
              type="text"
              value={searchName}
              onChange={(e) => setSearchName(e.target.value)}
              placeholder="Введите ФИО сотрудника"
            />
          </div>

          <div className="filter-group">
            <label>Фильтр по дате</label>
            <input
              type="date"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            />
          </div>
        </div>
      </div>

      <div className="employees-grid">
        {currentEmployees.map(employee => (
          <div key={employee.id} className="employee-card">
            <div className="card-content">
              <h3 className="employee-name">{employee.name}</h3>
              <p className="employee-date">{employee.date}</p>
              <span className={`status-badge ${getStatusClass(employee.status)}`}>
                {employee.status}
              </span>
            </div>
          </div>
        ))}
      </div>

      {totalPages > 1 && (
        <div className="pagination">
          <button
            onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
            disabled={currentPage === 1}
          >
            ←
          </button>

          {getPageNumbers().map((pageNum, index) => (
            pageNum === '...' ? (
              <span key={`ellipsis-${index}`} className="ellipsis">
                ...
              </span>
            ) : (
              <button
                key={pageNum}
                onClick={() => setCurrentPage(pageNum)}
                className={currentPage === pageNum ? 'active' : ''}
              >
                {pageNum}
              </button>
            )
          ))}

          <button
            onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
            disabled={currentPage === totalPages}
          >
            →
          </button>
        </div>
      )}

      <div className="results-counter">
        Показано {indexOfFirstItem + 1}-{Math.min(indexOfLastItem, filteredEmployees.length)} из {filteredEmployees.length} сотрудников
      </div>
    </div>
  );
}

export default App;
