function App() {
  const [selectedLocation, setSelectedLocation] = useState(null)
  const [selectedDate, setSelectedDate] = useState('')
  const [events, setEvents] = useState([])
  const [showLocationSelector, setShowLocationSelector] = useState(true)

  const handleLocationSelect = (location) => {
    setSelectedLocation(location)
    setShowLocationSelector(false)
  }

  const handleBackToLocations = () => {
    setShowLocationSelector(true)
    setSelectedLocation(null)
  }

  useEffect(() => {
    if (selectedLocation) {
      // Запрос для получения мероприятий по выбранной локации
      const fetchEvents = async () => {
        try {
          const response = await fetch(`http://192.168.1.65:8000/events/get_events/${selectedLocation.name}`);
          const data = await response.json();
          setEvents(data);
        } catch (error) {
          console.error("Ошибка при загрузке мероприятий:", error);
        }
      };

      fetchEvents();
    }
  }, [selectedLocation]);

  const filteredEvents = events.filter(event => {
    return event.location_name === selectedLocation?.name || event.location_name === 'Общее мероприятие'
  });

  return (
    <div className="app">
      {showLocationSelector ? (
        <LocationSelector 
          locations={locations} 
          onLocationSelect={handleLocationSelect} 
        />
      ) : (
        <EventsList 
          events={filteredEvents}
          selectedLocation={selectedLocation}
          selectedDate={selectedDate}
          onDateChange={setSelectedDate}
          onBackClick={handleBackToLocations}
        />
      )}
    </div>
  )
}

export default App
