import React, { useEffect, useState } from 'react';
import './profile.css'; // Подключаем правильный CSS для профиля

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [workDays, setWorkDays] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      if (!token) {
        setError('Нет токена авторизации');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch('http://192.168.1.66:8000/auth/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`Ошибка загрузки профиля: ${response.status}`);
        }

        const data = await response.json();
        setProfile(data.user);
        setWorkDays(data.work_days);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  return (
    <div className="container">
      <div className="main-content">
        <h1>Личный кабинет</h1>
        {loading ? (
          <p>Загрузка...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : profile ? (
          <>
            <div className="profile-header">
              <div className="profile-photo">
                {/* Если есть фото, то выводим его, иначе оставляем пустое место */}
                <img src={profile.image || 'https://placekitten.com/200/200'} alt="Фото пользователя" />
              </div>
              <div className="profile-info">
                <h2>{profile.fio}</h2>
                <p><strong>Должность:</strong> {profile.work_type}</p>
                <p><strong>Email:</strong> {profile.email}</p>
                <p><strong>Контакты:</strong> {profile.contacts?.join(', ') || 'Нет данных'}</p>
              </div>
            </div>

            {profile.description && (
              <div className="profile-description">
                <h3>Описание</h3>
                <p>{profile.description}</p>
              </div>
            )}

            <div className="workdays-container">
              <h3>Последние 5 рабочих дней</h3>
              {workDays.length > 0 ? (
                <table className="workdays-table">
                  <thead>
                    <tr>
                      <th>Дата</th>
                      <th>Статус</th>
                    </tr>
                  </thead>
                  <tbody>
                    {workDays.map((day, index) => (
                      <tr key={index}>
                        <td>{new Date(day.work_time).toLocaleString()}</td>
                        <td>{day.status === 1 ? 'Работал' : 'Отсутствовал'}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <p>Нет данных о рабочих днях</p>
              )}
            </div>
          </>
        ) : (
          <p>Нет данных</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
