import React, { useState, useEffect } from 'react';
import './css/home2.css';

const restaurantsData = [
  {
    id: 1,
    name: 'Русский двор',
    address: 'ул. Пушкина, д. 10',
    description: 'Традиционная русская кухня в уютной атмосфере старинного особняка. Домашние блюда, приготовленные по старинным рецептам.',
    imageUrl: 'https://images.unsplash.com/photo-1517248135467-4c7edcad34c4',
    lastEvent: {
      title: 'Фестиваль блинов',
      startDate: '2024-01-28',
      startTime: '12:00',
      shortDescription: 'Дегустация традиционных русских блинов с различными начинками'
    }
  },
  {
    id: 2,
    name: 'Итальянский дворик',
    address: 'пр. Ленина, д. 25',
    description: 'Аутентичная итальянская кухня, домашняя паста и пицца из дровяной печи. Уютная атмосфера и живая музыка по выходным.',
    imageUrl: 'https://images.unsplash.com/photo-1552566626-52f8b828add9',
    lastEvent: {
      title: 'Вечер итальянской кухни',
      startDate: '2024-01-29',
      startTime: '19:00',
      shortDescription: 'Специальное меню от шеф-повара'
    }
  },
  {
    id: 3,
    name: 'Азиатский сад',
    address: 'ул. Гагарина, д. 15',
    description: 'Паназиатская кухня в современной интерпретации. Суши, роллы, вок и многое другое в атмосфере восточного сада.',
    imageUrl: 'https://images.unsplash.com/photo-1552531268-3fe8c3fc8d84',
    lastEvent: {
      title: 'Мастер-класс по суши',
      startDate: '2024-01-30',
      startTime: '18:30',
      shortDescription: 'Обучение приготовлению суши от шеф-повара'
    }
  }
];

function App() {
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // Получаем данные о последних событиях
    fetch('http://192.168.1.66:8000/events/get_latest')
      .then(response => response.json())
      .then(data => {
        setEvents(data);
      })
      .catch(error => {
        console.error('Ошибка при получении событий:', error);
      });
  }, []);

  const handleCardClick = (restaurant) => {
    setSelectedRestaurant(restaurant);
  };

  const formatDate = (dateStr) => {
    return new Date(dateStr).toLocaleDateString('ru-RU');
  };

  return (
    <div className="container">
      <header>
        <h1>Добро пожаловать в наш ресторанный портал</h1>
      </header>

      <h2>Рестораны</h2>

      <div className="restaurants-grid">
        {restaurantsData.map(restaurant => (
          <div 
            key={restaurant.id} 
            className="restaurant-card"
            onClick={() => handleCardClick(restaurant)} // Обработчик клика
          >
            <img 
              src={restaurant.imageUrl} 
              alt={restaurant.name} 
              className="restaurant-image"
            />
            <div className="restaurant-info">
              <h2>{restaurant.name}</h2>
              <p>{restaurant.address}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedRestaurant && (
        <div className="modal-overlay" onClick={() => setSelectedRestaurant(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button 
              className="close-button"
              onClick={() => setSelectedRestaurant(null)}
            >
              ×
            </button>

            <img 
              src={selectedRestaurant.imageUrl} 
              alt={selectedRestaurant.name} 
              className="modal-image"
            />

            <div className="modal-info">
              <h2>{selectedRestaurant.name}</h2>
              <p className="address">{selectedRestaurant.address}</p>
              <p className="description">{selectedRestaurant.description}</p>

              <div className="event-section">
                <h3>Последнее событие</h3>
                <div className="event-card">
                  <h4>{selectedRestaurant.lastEvent.title}</h4>
                  <p className="event-date">
                    {formatDate(selectedRestaurant.lastEvent.startDate)}, {selectedRestaurant.lastEvent.startTime}
                  </p>
                  <p className="event-description">
                    {selectedRestaurant.lastEvent.shortDescription}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="latest-events-section">
        <h3>Последние события</h3>
        {events.length > 0 ? (
          events.map(event => (
            <div className="event-card" key={event.id}>
              <h4>{event.name}</h4>
              <p className="event-date">
                {formatDate(event.date_start)}
              </p>
              <p className="event-description">
                {event.description}
              </p>
            </div>
          ))
        ) : (
          <p>Загружаем события...</p>
        )}
      </div>
    </div>
  );
}

export default App;
