// Updated React JSX with unique CSS class names
import React, { useState } from 'react';
import './css/manualsApp.css';

function ManualModal({ manual, onClose }) {
  return (
    <div className="modal-overlay-custom" onClick={onClose}>
      <div className="modal-content-custom" onClick={(e) => e.stopPropagation()}>
        <h2 className="modal-title-custom">{manual.title}</h2>
        <p className="modal-location-custom">Location: {manual.location}</p>
        <p className="modal-category-custom">Category: {manual.category}</p>
        <img src={manual.image} alt={manual.title} className="modal-image-custom" />
        <a 
          href={manual.link} 
          target="_blank" 
          rel="noopener noreferrer" 
          className="modal-link-custom"
        >
          Open Manual
        </a>
        <button className="modal-close-btn-custom" onClick={onClose}>Close</button>
      </div>
    </div>
  );
}

function ManualsList({ manuals, selectedLocation, onBackClick }) {
  const [selectedManual, setSelectedManual] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');

  const categories = ['Kitchen', 'Hookah', 'Bar', 'Host'];

  const filteredManuals = manuals.filter((manual) => {
    const searchMatch = manual.title.toLowerCase().includes(searchQuery.toLowerCase());
    const categoryMatch = categoryFilter === 'all' || manual.category === categoryFilter;
    const locationMatch = !selectedLocation || manual.location === selectedLocation.name;
    return searchMatch && categoryMatch && locationMatch;
  });

  return (
    <div className="manuals-list-container">
      <div className="filters-section">
        <input
          type="text"
          placeholder="Search by title"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input-custom"
        />
        <select
          value={categoryFilter}
          onChange={(e) => setCategoryFilter(e.target.value)}
          className="category-filter-dropdown"
        >
          <option value="all">All Categories</option>
          {categories.map((category) => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>
        <button className="back-btn-custom" onClick={onBackClick}>Back to Locations</button>
      </div>

      <div className="manuals-grid-container">
        {filteredManuals.map((manual) => (
          <div 
            key={manual.id} 
            className="manual-card-custom"
            onClick={() => setSelectedManual(manual)}
          >
            <img src={manual.image} alt={manual.title} className="manual-preview-img" />
            <div className="manual-details">
              <h3 className="manual-title-custom">{manual.title}</h3>
              <p className="manual-category-custom">{manual.category}</p>
              <p className="manual-location-custom">{manual.location}</p>
            </div>
          </div>
        ))}
      </div>

      {selectedManual && (
        <ManualModal 
          manual={selectedManual} 
          onClose={() => setSelectedManual(null)} 
        />
      )}
    </div>
  );
}

function LocationSelector({ locations, onLocationSelect }) {
  return (
    <div className="location-selector-container">
      <h1 className="location-selector-title">Select a Location</h1>
      <div className="locations-grid-container">
        {locations.map((location) => (
          <div 
            key={location.id} 
            className="location-card-custom"
            onClick={() => onLocationSelect(location)}
          >
            <img src={location.image} alt={location.name} className="location-card-img" />
            <h2 className="location-card-title">{location.name}</h2>
            <p className="location-card-description">{location.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

const App = () => {
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [showLocationSelector, setShowLocationSelector] = useState(true);

  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
    setShowLocationSelector(false);
  };

  const handleBackToLocations = () => {
    setShowLocationSelector(true);
    setSelectedLocation(null);
  };

  return (
    <div className="app-container">
      {showLocationSelector ? (
        <LocationSelector 
          locations={locations} 
          onLocationSelect={handleLocationSelect} 
        />
      ) : (
        <ManualsList 
          manuals={manuals}
          selectedLocation={selectedLocation}
          onBackClick={handleBackToLocations}
        />
      )}
    </div>
  );
};

export default App;
